import{_ as a,c as i,o as e,a4 as s}from"./chunks/framework.DRiVJl7O.js";const g=JSON.parse('{"title":"Installation","description":"","frontmatter":{},"headers":[],"relativePath":"guide/installation.md","filePath":"guide/installation.md"}'),l={name:"guide/installation.md"},t=s(`<h1 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h1><h2 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h2><h3 id="install-manually" tabindex="-1">Install Manually <a class="header-anchor" href="#install-manually" aria-label="Permalink to &quot;Install Manually&quot;">​</a></h3><ul><li>For 64-bit installation package, please download <code>Setup windows_x64 (.exe)</code>, and double-click to install after downloading.</li><li>For 64-bit portable edition, please download <code>Portable windows_x64 (.zip)</code>, and double-click to install after downloading.</li><li>For ARM64 installation package, please download <code>Setup windows_arm64 (.exe)</code>, and double-click to install after downloading.</li><li>For ARM64 portable edition, please download <code>Portable windows_arm64 (.zip)</code>, and double-click to install after downloading.</li></ul><div class="danger custom-block"><p class="custom-block-title">Troubleshooting</p><p>If you receive a prompt that you need to install the Webview runtime, but you cannot download it, you may need to download and install the <a href="https://developer.microsoft.com/en-us/microsoft-edge/webview2/" target="_blank" rel="noreferrer">Webview2 Runtime</a> manually.</p></div><h2 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h2><h3 id="install-manually-1" tabindex="-1">Install Manually <a class="header-anchor" href="#install-manually-1" aria-label="Permalink to &quot;Install Manually&quot;">​</a></h3><ul><li>If you are using a Mac with M1/M2/M3 chip, please download <code>mac_arm64 (.dmg)</code></li><li>If you are using a Mac with Intel chip, please download <code>mac_intel (.dmg)</code></li></ul><p>After downloading, open the <code>.dmg</code> file and drag <code>Tiny RDM.app</code> into the <code>Applications</code> folder.</p><div class="danger custom-block"><p class="custom-block-title">Troubleshooting</p><ul><li>Since the app is not signed, shows &quot;The developer cannot be verified&quot;:</li></ul><p>Click the <code>Cancel</code> button, then go to <code>Settings -&gt; Privacy &amp; Security</code> page, click the <code>Still Open</code> button, and then click the <code>Open</code> button in the pop-up window.</p><ul><li>If you get a pop-up such as &quot;Broken files&quot; after installation, please open <code>Terminal.app</code> and execute the following command:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> xattr</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -d</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> com.apple.quarantine</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /Applications/Tiny</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">\\ </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">RDM.app</span></span></code></pre></div><p>Then try reopen <code>Tiny RDM</code>.</p><ul><li>Additionally, the <code>dmg</code> installation package also includes a <code>Repair</code> script, which you can double-click to execute and enter the administrator password to quickly fix the above issues.</li></ul></div><h2 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h2><h3 id="install-manually-2" tabindex="-1">Install Manually <a class="header-anchor" href="#install-manually-2" aria-label="Permalink to &quot;Install Manually&quot;">​</a></h3><ul><li>For <code>deb</code> installation package, download <code>linux_amd64.deb (.deb)</code></li><li>For <code>AppImage</code> package, download <code>linux_amd64.AppImage (.AppImage)</code></li></ul><p>Install <code>deb</code> package using <code>apt-get</code>:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./tiny_rdm_{version}_linux_amd64.deb</span></span></code></pre></div><p>Use <code>AppImage</code>:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">chmod</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> u+x</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> tiny_rdm_{version}_linux_amd64.AppImage</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">./tiny_rdm_</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">{version}</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">_linux_amd64.AppImage</span></span></code></pre></div><div class="danger custom-block"><p class="custom-block-title">Troubleshooting</p><p>In the latest version of <a href="https://archlinux.org/packages/extra/x86_64/webkit2gtk" target="_blank" rel="noreferrer">Webkit2Gtk</a> (2.42.0), due to incomplete implementation of DMABUF by Nvidia proprietary drivers, there may be issues with startup failure and crashes. If you encounter a &quot;Segmentation fault&quot; or Pot fails to start, please try adding the environment variable <code>WEBKIT_DISABLE_DMABUF_RENDERER=1</code> in <code>/etc/environment</code> (or any other place where environment variables can be set) to disable the use of DMABUF.</p></div><h2 id="build-guidelines" tabindex="-1">Build Guidelines <a class="header-anchor" href="#build-guidelines" aria-label="Permalink to &quot;Build Guidelines&quot;">​</a></h2><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>Go (latest version)</li><li>Node.js &gt;= 16</li><li>NPM &gt;= 9</li></ul><h3 id="install-wails" tabindex="-1">Install wails <a class="header-anchor" href="#install-wails" aria-label="Permalink to &quot;Install wails&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">go</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> github.com/wailsapp/wails/v2/cmd/wails@latest</span></span></code></pre></div><h3 id="pull-the-code" tabindex="-1">Pull the Code <a class="header-anchor" href="#pull-the-code" aria-label="Permalink to &quot;Pull the Code&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> clone</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://github.com/tiny-craft/tiny-rdm</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --depth=1</span></span></code></pre></div><h3 id="build-frontend" tabindex="-1">Build Frontend <a class="header-anchor" href="#build-frontend" aria-label="Permalink to &quot;Build Frontend&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">npm</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --prefix</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./frontend</span></span></code></pre></div><h3 id="compile-and-run" tabindex="-1">Compile and Run <a class="header-anchor" href="#compile-and-run" aria-label="Permalink to &quot;Compile and Run&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wails</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dev</span></span></code></pre></div><h3 id="build-installation-package" tabindex="-1">Build Installation Package <a class="header-anchor" href="#build-installation-package" aria-label="Permalink to &quot;Build Installation Package&quot;">​</a></h3><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wails</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> build</span></span></code></pre></div>`,31),n=[t];function o(d,h,p,c,r,u){return e(),i("div",null,n)}const b=a(l,[["render",o]]);export{g as __pageData,b as default};
